// Inputs
const webhookData = $('Webhook').first();
const requestedDate = webhookData ? webhookData.json.query.date : null;

if (!requestedDate) {
  return { slots: [] };
}

// Safely get bookings (default to empty array if none found)
let dbBookings = [];
let gCalBusy = [];

try {
  dbBookings = $('Supabase').all().map(item => item.json);
} catch(e) {}

try {
  gCalBusy = $('Google Calendar').all().map(item => item.json);
} catch(e) {}

// SLOT LOCK FILTERING: Only consider bookings that actually block availability
// 1. Confirmed/completed bookings always block
// 2. Pending bookings with active (non-expired) locks block
const now = Date.now();
const blockedBookings = dbBookings.filter(booking => {
  // Confirmed/completed always block
  if (booking.status === 'confirmed' || booking.status === 'completed') {
    return true;
  }
  // Pending with active lock blocks
  if (booking.status === 'pending' && booking.slot_lock_expires_at) {
    const lockExpiry = new Date(booking.slot_lock_expires_at).getTime();
    return lockExpiry > now;
  }
  // Cancelled or expired locks don't block
  return false;
});

// Configuration: Clinic Hours (09:00 - 17:00)
const startHour = 9;
const endHour = 17;
const slotDurationMinutes = 60;

// Generate slots as simple strings first to avoid timezone confusion
const slots = [];
for (let h = startHour; h < endHour; h++) {
  const timeStr = `${h.toString().padStart(2, '0')}:00`;
  const slotIso = `${requestedDate}T${timeStr}:00`;
  slots.push({ time: timeStr, iso: slotIso, available: true });
}

// Helper: Check overlap
const isBusy = (slotIso, busyStartIso, busyEndIso) => {
  if (!busyStartIso || !busyEndIso) return false;
  const slotStart = new Date(slotIso).getTime();
  const slotEnd = slotStart + (slotDurationMinutes * 60 * 1000);
  const busyStart = new Date(busyStartIso).getTime();
  const busyEnd = new Date(busyEndIso).getTime();
  return (slotStart < busyEnd && slotEnd > busyStart);
};

// Filter availability
const finalSlots = slots.map(slot => {
  let isTaken = false;

  // 1. Check DB (only blocked bookings)
  for (const booking of blockedBookings) {
    if (booking && isBusy(slot.iso, booking.start_time, booking.end_time)) {
      isTaken = true; break;
    }
  }

  // 2. Check GCal
  if (!isTaken) {
    for (const event of gCalBusy) {
      if (event) {
        const start = event.start?.dateTime || event.start?.date;
        const end = event.end?.dateTime || event.end?.date;
        if (isBusy(slot.iso, start, end)) {
          isTaken = true; break;
        }
      }
    }
  }

  return { time: slot.time, available: !isTaken };
});

return { slots: finalSlots };
